import {data} from '../data.js';
import {useState} from 'react'
export const AllEmployees=()=>{
    
   
   const [allEmployees,setAllEmployees]=useState({});
   
   const handleChange=(e)=>{
   const {name,value}=e.target;
   setAllEmployees((prev)=>{
    return {
        ...prev,
        [name]:value
    } 
   })}
   console.log(allEmployees.search)

    return(
        <section>
            <div className="">
                <br/><br/><br/>
                <div>constainsicon Employee</div>
                <div>Employee/All Employee</div>
                <span>Employee n Total</span>
                <form>
                    <input type="text" placeholder="search" onChange={handleChange}name="search" value={allEmployees.search}/>
                </form>
                <table style={{ border: 3, bordercolor: "black" }}>
                    <thead>
                        <tr>
                            <th>RegNo.</th>
                            <th>eName</th>
                            <th>Type</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        
                        {data.filter((item)=>{
                            return allEmployees.search.toLowerCase()===''? item:
                            item.first_name.toLowerCase().includes(allEmployees.search);
                        }).map((item)=>(
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.first_name}  {item.last_name}</td>
                                <td>{item.email}</td>
                                <td>{item.gender}</td>
                                
                            </tr>
                        ))}
                    </tbody>
                </table>
                </div>
        </section>
    )
}
